    render() {

        return (
            <>
                <AppBar position="static">
                    <Toolbar>
                        <Typography variant="h4">
                            Hogwarts
                        </Typography>
                        <Button color="inherit" className="addStudentBtn"  >Add Student /Professor</Button>
                    </Toolbar>
                </AppBar>
                <Dialog open={this.state.open} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title"> Create Student</DialogTitle>
                    <DialogContent>
                        <FormControl fullWidth>
                            <InputLabel htmlFor="component-simple">First Name</InputLabel>
                            <Input id="firstname" value={this.state.firstName} />
                        </FormControl>
                        <FormControl fullWidth>
                            <InputLabel htmlFor="component-simple">Last Name</InputLabel>
                            <Input id="lastname" value={this.state.lastName} />
                        </FormControl>
                        {/* <FormControl component="fieldset">
                            <FormLabel component="legend">Gender</FormLabel>
